// Algoritmo para encontrar a interseção de duas arrays

function intersecao(arr1, arr2) {
    // Cria uma array vazia para armazenar os elementos de interseção
    let resultado = [];

    // Percorre todos os elementos da arr1
    for (let i = 0; i < arr1.length; i++) {
        // Percorre todos os elementos da arr2
        for (let j = 0; j < arr2; j++){
            // Verifica se os elementos da arr1 e arr2 são iguais
            if (arr1[i] === arr2[j]) {
                // Se forem iguais, adiciona o elemento na array resultado
                resultado.push(arr1[i]);
            }
        }
    }

    // Retorna a array resultado
    return resultado;
}

