def binary_search(arr, alvo):
    esquerda = 0
    direita = len(arr)-1
    while esquerda <= direita:
        meio = (esquerda+direita) // 2
        if arr[meio] == alvo:
            return meio
        elif alvo < arr[meio]:
            direita = meio - 1
        else:
            esquerda = meio + 1

    return -1

